manifest.install:
DROP TABLE IF EXISTS `ims_ybsc_activity`;
CREATE TABLE `ims_ybsc_activity` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `type` int(2) DEFAULT '1' COMMENT '1miaosha',
  `oprice` decimal(10,2) DEFAULT NULL,
  `nprice` decimal(10,2) DEFAULT NULL,
  `pic` varchar(255) DEFAULT NULL,
  `img` text,
  `stime` varchar(12) DEFAULT NULL,
  `etime` varchar(12) DEFAULT NULL,
  `base_sell` int(5) DEFAULT '0',
  `all_sell` int(5) DEFAULT '0',
  `max_pre` int(5) DEFAULT '0' COMMENT '0wuxian',
  `status` int(1) DEFAULT '1' COMMENT '1ok2no',
  `mch_id` int(5) DEFAULT NULL,
  `goods_id` int(5) DEFAULT NULL,
  `sort` int(11) NOT NULL DEFAULT '0' COMMENT '排序：越大越靠前',
  `other` text COMMENT 'qita',
  `is_del` int(1) DEFAULT '2' COMMENT '1del2no',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of ims_ybsc_activity
-- ----------------------------

-- ----------------------------
-- Table structure for `ims_ybsc_activity_order`
-- ----------------------------
DROP TABLE IF EXISTS `ims_ybsc_activity_order`;
CREATE TABLE `ims_ybsc_activity_order` (
  `order_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '订单id',
  `order_no` varchar(255) DEFAULT '' COMMENT '订单编号',
  `out_trade_no` varchar(100) NOT NULL DEFAULT '0' COMMENT '外部交易号',
  `pay_type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '支付类型',
  `activity_order_type` int(11) NOT NULL DEFAULT '0' COMMENT '活动类型0: 默认 1:砍价活动 3.秒杀',
  `shipping_type` tinyint(4) NOT NULL DEFAULT '1' COMMENT '订单配送方式',
  `buyer_id` int(11) NOT NULL COMMENT '买家id',
  `user_name` varchar(50) NOT NULL DEFAULT '' COMMENT '买家会员名称',
  `buyer_message` varchar(255) DEFAULT '' COMMENT '买家附言',
  `receiver_mobile` varchar(11) NOT NULL DEFAULT '' COMMENT '收货人的手机号码',
  `receiver_area` int(11) NOT NULL COMMENT '收货人所在省',
  `receiver_address` varchar(255) NOT NULL DEFAULT '' COMMENT '收货人详细地址',
  `receiver_zip` varchar(6) DEFAULT '' COMMENT '收货人邮编',
  `receiver_name` varchar(50) NOT NULL DEFAULT '' COMMENT '收货人姓名',
  `order_money` decimal(10,2) NOT NULL COMMENT '订单总价',
  `shipping_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单运费',
  `pay_money` decimal(10,2) NOT NULL COMMENT '订单实付金额',
  `refund_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单退款金额',
  `order_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '-1  取消0   待付款 1  待发货2 已发货3 已完成4 退款中5已退款',
  `pay_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '订单付款状态',
  `shipping_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '订单配送状态',
  `shipping_company_id` int(11) NOT NULL DEFAULT '0' COMMENT '配送物流公司ID',
  `pay_time` int(11) DEFAULT '0' COMMENT '订单付款时间',
  `sign_time` int(11) DEFAULT '0' COMMENT '买家签收时间',
  `consign_time` int(11) DEFAULT '0' COMMENT '卖家发货时间',
  `create_time` int(11) DEFAULT '0' COMMENT '订单创建时间',
  `finish_time` int(11) DEFAULT '0' COMMENT '订单完成时间',
  `is_deleted` int(1) NOT NULL DEFAULT '0' COMMENT '订单是否已删除',
  `refund_time` int(20) NOT NULL COMMENT '退款申请时间',
  `seller_memo` varchar(255) NOT NULL COMMENT '卖家备注',
  `mch_id` int(11) NOT NULL DEFAULT '0' COMMENT '商户id',
  `bargain_id` int(11) DEFAULT NULL COMMENT '活动商品id',
  `bargain_name` varchar(1000) NOT NULL COMMENT '商品名称',
  `bargain_pic` varchar(1000) NOT NULL COMMENT '商品主图',
  `original_price` decimal(10,2) NOT NULL,
  `total` int(11) NOT NULL COMMENT '购买数量',
  `mailing_type` int(11) NOT NULL DEFAULT '1' COMMENT '1/邮寄 2/自提',
  `mention_time` int(11) NOT NULL DEFAULT '0' COMMENT '自提时间',
  `refund_status` int(11) NOT NULL DEFAULT '0' COMMENT '退款状态',
  PRIMARY KEY (`order_id`) USING BTREE,
  KEY `UK_ns_order_buyer_id` (`buyer_id`) USING BTREE,
  KEY `UK_ns_order_order_no` (`order_no`) USING BTREE,
  KEY `UK_ns_order_pay_status` (`pay_status`) USING BTREE,
  KEY `UK_ns_order_status` (`order_status`) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of ims_ybsc_activity_order
-- ----------------------------

-- ----------------------------
-- Table structure for `ims_ybsc_activity_order_payment`
-- ----------------------------
DROP TABLE IF EXISTS `ims_ybsc_activity_order_payment`;
CREATE TABLE `ims_ybsc_activity_order_payment` (
  `out_trade_no` varchar(30) NOT NULL COMMENT '支付单编号',
  `type_alis_id` int(11) NOT NULL DEFAULT '0' COMMENT '订单类型关联ID',
  `pay_body` varchar(255) NOT NULL DEFAULT '' COMMENT '订单支付简介',
  `pay_detail` varchar(1000) NOT NULL DEFAULT '' COMMENT '订单支付详情',
  `pay_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '支付金额',
  `pay_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '支付状态',
  `pay_type` int(11) NOT NULL DEFAULT '1' COMMENT '支付方式',
  `create_time` int(11) DEFAULT '0' COMMENT '创建时间',
  `pay_time` int(11) DEFAULT '0' COMMENT '支付时间',
  KEY `IDX_ns_order_payment_out_trade_no` (`out_trade_no`) USING BTREE,
  KEY `IDX_ns_order_payment_pay_status` (`pay_status`) USING BTREE,
  KEY `IDX_ns_order_payment_pay_type` (`pay_type`) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of ims_ybsc_activity_order_payment
-- ----------------------------

-- ----------------------------
-- Table structure for `ims_ybsc_application`
-- ----------------------------
DROP TABLE IF EXISTS `ims_ybsc_application`;
CREATE TABLE `ims_ybsc_application` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `agents_name` varchar(255) DEFAULT NULL COMMENT '昵称',
  `agents_key` varchar(255) DEFAULT NULL COMMENT 'key',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `app_id` varchar(255) NOT NULL COMMENT 'APP_ID',
  `app_secret` varchar(255) NOT NULL COMMENT 'APP_SECRET',
  `app_mchid` varchar(255) NOT NULL COMMENT 'APP_MCHID',
  `app_url` varchar(255) NOT NULL COMMENT 'APP_URL',
  `app_key` varchar(255) NOT NULL COMMENT 'APP_KEY',
  `is_use` int(11) NOT NULL DEFAULT '0' COMMENT '0:正常1：禁用',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='应用表';

-- ----------------------------
-- Records of ims_ybsc_application
-- ----------------------------

-- ----------------------------
-- Table structure for `ims_ybsc_application_activity_class`
-- ----------------------------
DROP TABLE IF EXISTS `ims_ybsc_application_activity_class`;
CREATE TABLE `ims_ybsc_application_activity_class` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `agents_id` int(11) DEFAULT NULL COMMENT '应用id',
  `class_name` varchar(255) DEFAULT NULL COMMENT '分类名称',
  `sort` int(11) DEFAULT '0',
  `create_time` int(11) DEFAULT NULL COMMENT '添加时间',
  `is_use` int(11) DEFAULT '0' COMMENT '0：正常1：禁用',
  `img_url` varchar(255) NOT NULL COMMENT '图片',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='应用分类表';

