DROP TABLE IF EXISTS `ims_ybsc_bargain_order`;
CREATE TABLE `ims_ybsc_bargain_order` (
  `order_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '订单id',
  `order_no` varchar(255) DEFAULT '' COMMENT '订单编号',
  `out_trade_no` varchar(100) NOT NULL DEFAULT '0' COMMENT '外部交易号',
  `pay_type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '支付类型',
  `activity_order_type` int(11) NOT NULL DEFAULT '0' COMMENT '活动类型0: 默认 1:砍价活动 2:满减活动 3:新用户',
  `shipping_type` tinyint(4) NOT NULL DEFAULT '1' COMMENT '订单配送方式',
  `buyer_id` int(11) NOT NULL COMMENT '买家id',
  `user_name` varchar(50) NOT NULL DEFAULT '' COMMENT '买家会员名称',
  `buyer_message` varchar(255) DEFAULT '' COMMENT '买家附言',
  `receiver_mobile` varchar(11) NOT NULL DEFAULT '' COMMENT '收货人的手机号码',
  `receiver_area` int(11) NOT NULL COMMENT '收货人所在省',
  `receiver_address` varchar(255) NOT NULL DEFAULT '' COMMENT '收货人详细地址',
  `receiver_zip` varchar(6) DEFAULT '' COMMENT '收货人邮编',
  `receiver_name` varchar(50) NOT NULL DEFAULT '' COMMENT '收货人姓名',
  `order_money` decimal(10,2) NOT NULL COMMENT '订单总价',
  `shipping_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单运费',
  `pay_money` decimal(10,2) NOT NULL COMMENT '订单实付金额',
  `refund_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单退款金额',
  `order_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '-1  取消0   待付款 1  待发货2 已发货3 已完成4 退款中5已退款',
  `pay_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '订单付款状态',
  `shipping_status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '订单配送状态',
  `shipping_company_id` int(11) NOT NULL DEFAULT '0' COMMENT '配送物流公司ID',
  `pay_time` int(11) DEFAULT '0' COMMENT '订单付款时间',
  `sign_time` int(11) DEFAULT '0' COMMENT '买家签收时间',
  `consign_time` int(11) DEFAULT '0' COMMENT '卖家发货时间',
  `create_time` int(11) DEFAULT '0' COMMENT '订单创建时间',
  `finish_time` int(11) DEFAULT '0' COMMENT '订单完成时间',
  `is_deleted` int(1) NOT NULL DEFAULT '0' COMMENT '订单是否已删除',
  `refund_time` int(20) NOT NULL COMMENT '退款申请时间',
  `seller_memo` varchar(255) NOT NULL COMMENT '卖家备注',
  `mch_id` int(11) NOT NULL DEFAULT '0' COMMENT '商户id',
  `bargain_id` int(11) DEFAULT NULL COMMENT '活动商品id',
  `bargain_name` varchar(1000) NOT NULL COMMENT '商品名称',
  `bargain_pic` varchar(1000) NOT NULL COMMENT '商品主图',
  `original_price` decimal(10,2) NOT NULL,
  `total` int(11) NOT NULL COMMENT '购买数量',
  `mailing_type` int(11) NOT NULL DEFAULT '1' COMMENT '1/邮寄 2/自提',
  `mention_time` int(11) NOT NULL DEFAULT '0' COMMENT '自提时间',
  `refund_status` int(11) NOT NULL DEFAULT '0' COMMENT '退款状态',
  PRIMARY KEY (`order_id`) USING BTREE,
  KEY `UK_ns_order_buyer_id` (`buyer_id`) USING BTREE,
  KEY `UK_ns_order_order_no` (`order_no`) USING BTREE,
  KEY `UK_ns_order_pay_status` (`pay_status`) USING BTREE,
  KEY `UK_ns_order_status` (`order_status`) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8 AVG_ROW_LENGTH=440 ROW_FORMAT=DYNAMIC COMMENT='订单表';

