-- Table structure for `ims_ybsc_cart`
-- ----------------------------
DROP TABLE IF EXISTS `ims_ybsc_cart`;
CREATE TABLE `ims_ybsc_cart` (
  `cart_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '购物车id',
  `buyer_id` int(11) NOT NULL DEFAULT '0' COMMENT '买家id',
  `goods_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品id',
  `goods_name` varchar(200) NOT NULL COMMENT '商品名称',
  `sku_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品的skuid',
  `num` smallint(5) NOT NULL DEFAULT '1' COMMENT '购买商品数量',
  `goods_images` int(11) NOT NULL DEFAULT '0' COMMENT '商品图片',
  `mch_id` int(11) NOT NULL DEFAULT '0' COMMENT '商户id',
  `create_time` int(11) NOT NULL COMMENT '加入购物车时间',
  PRIMARY KEY (`cart_id`) USING BTREE,
  KEY `member_id` (`buyer_id`) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8 AVG_ROW_LENGTH=1170 ROW_FORMAT=COMPACT COMMENT='购物车表';

