  `show_sea` int(1) DEFAULT '1',
  `is_auth_code` int(1) DEFAULT '2' COMMENT '企业微信授权码 1/打开 2/关闭',
  PRIMARY KEY (`id`),
  UNIQUE KEY `mch_id` (`mch_id`) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

