DROP TABLE IF EXISTS `ims_ybsc_goods`;
CREATE TABLE `ims_ybsc_goods` (
  `goods_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '商品id',
  `goods_name` varchar(255) NOT NULL COMMENT '商品名称',
  `cate_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品分类id',
  `brand_id` int(11) NOT NULL DEFAULT '0' COMMENT '品牌id',
  `market_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '市场价',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '原价',
  `promotion_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '促销价',
  `cost_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '成本价',
  `clicks` int(11) NOT NULL DEFAULT '0' COMMENT '商品点击',
  `shipping_fee` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '运费 0为免运费',
  `shipping_fee_id` int(11) NOT NULL DEFAULT '0' COMMENT '运费模版',
  `sales` int(11) NOT NULL DEFAULT '0' COMMENT '销量',
  `real_sales` int(11) NOT NULL DEFAULT '0' COMMENT '实际销量',
  `images` int(11) NOT NULL DEFAULT '0' COMMENT '商品主图',
  `keywords` varchar(255) NOT NULL COMMENT '商品关键字',
  `introduction` varchar(255) DEFAULT NULL COMMENT '商品简介',
  `description` longtext,
  `stock` int(11) unsigned NOT NULL COMMENT '商品库存',
  `is_new` int(1) NOT NULL DEFAULT '0' COMMENT '是否新品',
  `is_hot` int(1) NOT NULL DEFAULT '0' COMMENT '是否热销',
  `is_recommend` int(1) NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `state` tinyint(3) NOT NULL DEFAULT '1' COMMENT '商品状态 0下架 1 正常 10违规',
  `img_id_array` varchar(255) DEFAULT NULL COMMENT '图片序列',
  `goods_attribute_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品类型',
  `goods_attribute` text COMMENT '商品属性',
  `goods_spec_format` text COMMENT '商品规格',
  `supplier_id` int(11) NOT NULL DEFAULT '0' COMMENT '供货商',
  `sale_date` int(11) DEFAULT '0' COMMENT '上架时间',
  `create_time` int(11) DEFAULT '0' COMMENT '添加时间',
  `update_time` int(11) DEFAULT '0' COMMENT '编辑时间',
  `min_buy` int(11) NOT NULL COMMENT '最少购买',
  `sort` int(11) DEFAULT '0' COMMENT '商品排序',
  `is_del` int(11) NOT NULL DEFAULT '0' COMMENT '商品逻辑删除',
  `mch_id` int(11) NOT NULL DEFAULT '0' COMMENT '商户id',
  `barcode` varchar(255) NOT NULL COMMENT '条形码',
  `weight` int(11) DEFAULT '0' COMMENT '重量(g)',
  `goods_type` int(11) NOT NULL DEFAULT '1' COMMENT '1/实体商品 2/虚拟商品',
  `max_buy` int(11) NOT NULL COMMENT '最多购买',
  PRIMARY KEY (`goods_id`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='商品表';

