  `bucket` varchar(255) DEFAULT '' COMMENT '存储空间名称',
  PRIMARY KEY (`img_id`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 AVG_ROW_LENGTH=204 ROW_FORMAT=COMPACT COMMENT='相册图片表';

