-- Table structure for `ims_ybsc_user`
-- ----------------------------
DROP TABLE IF EXISTS `ims_ybsc_user`;
CREATE TABLE `ims_ybsc_user` (
  `uid` int(11) NOT NULL AUTO_INCREMENT COMMENT '会员uid',
  `user_headimg` varchar(255) DEFAULT '' COMMENT '会员头像',
  `wx_openid` varchar(255) DEFAULT '' COMMENT '微信用户openid',
  `nick_name` varchar(255) DEFAULT '' COMMENT '用户昵称',
  `reg_time` int(11) DEFAULT '0' COMMENT '注册时间',
  `mch_id` int(11) NOT NULL DEFAULT '0' COMMENT '商户id',
  `agents_id` int(11) NOT NULL DEFAULT '0' COMMENT '代理商id',
  `integral` int(11) NOT NULL DEFAULT '0' COMMENT '会员积分',
  `consume` int(11) NOT NULL DEFAULT '0' COMMENT '总累计积分',
  `level_id` int(11) NOT NULL DEFAULT '0' COMMENT '会员等级',
  `is_distributor` int(11) NOT NULL DEFAULT '0' COMMENT '是否分销商0不是；1是；2申请中',
  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '父级id',
  `total_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '累计佣金',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '可提现佣金',
  `wx_unionid` varchar(255) DEFAULT NULL,
  `user_type` int(11) NOT NULL DEFAULT '1' COMMENT '1微信；2百度',
  `is_writeoff` int(11) NOT NULL DEFAULT '2' COMMENT '核销员 1/是  2/否',
  PRIMARY KEY (`uid`) USING BTREE,
  UNIQUE KEY `wx_openid` (`wx_openid`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='会员表';

