-- ----------------------------
-- Table structure for `ims_ybsc_user_address`
-- ----------------------------
DROP TABLE IF EXISTS `ims_ybsc_user_address`;
CREATE TABLE `ims_ybsc_user_address` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL COMMENT '会员关联ID',
  `consigner` varchar(255) NOT NULL DEFAULT '' COMMENT '收件人',
  `phone` varchar(11) NOT NULL DEFAULT '' COMMENT '手机',
  `area` int(11) NOT NULL DEFAULT '0' COMMENT '地区',
  `address` varchar(255) NOT NULL DEFAULT '' COMMENT '详细地址',
  `zip_code` varchar(6) DEFAULT '' COMMENT '邮编',
  `is_default` int(11) NOT NULL DEFAULT '0' COMMENT '默认收货地址',
  `create_time` int(11) NOT NULL COMMENT '添加时间',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `IDX_member_express_address_uid` (`uid`) USING BTREE
