<?php
 namespace app\admin\service; class Article extends Base { function __construct() { parent::__construct(); $this->article = new \app\common\model\Article(); } public function getArticleList($condition = '', $search_text = '', $order = '') { $list = $this->article->getPageLisy($condition, $search_text, $order); return $list; } public function addArticle($data) { $article = new \app\common\model\Article(); return $article->save($data); } public function editArticle($data, $article_id) { $article = new \app\common\model\Article(); return $article->save($data, ["\x61\162\x74\x69\143\x6c\145\137\x69\144" => $article_id]); } public function delArticle($data, $article_id) { $article = new \app\common\model\Article(); return $article->save($data, ["\141\162\x74\x69\x63\154\145\137\151\144" => $article_id]); } }
