class model_goodsort {



	static function getSort( $plug = 0 ){
		global $_W;

		$cache = Util::getCache('goodsort','all'.$plug);
		if( empty( $cache ) ){
			$where = array('uniacid'=>$_W['uniacid'],'status'=>0,'parent'=>0,'plug'=>$plug);
			$data = Util::getAllDataInSingleTable('zofui_sitetemp_goodsort',$where,1,1010,' `number` DESC ',false,false);
			
			if( !empty( $data[0] ) ) {
				foreach ( $data[0] as &$v ) {
					$where = array('uniacid'=>$_W['uniacid'],'status'=>0,'parent'=>$v['id']);
					$down = Util::getAllDataInSingleTable('zofui_sitetemp_goodsort',$where,1,1010,' `number` DESC ',false,false);
					$v['down'] = $down[0];
				}
				unset( $v );
			}
			
			$cache =  $data[0];
			Util::setCache('goodsort','all'.$plug,$cache);
		}
		return $cache;
	}
	


